import React, {Component} from 'react';
import {connect} from "react-redux"
import AlbumDetails from "./AlbumDetails"
import {getOneAlbum} from "../actions"



class AlbumContainer extends Component {
    componentDidMount(){
        const { albumId } = this.props.match.params
        console.log(this.props.album.length !== 0 ? this.props.album : null)
        this.props.getOneAlbum(albumId)

    }
    getAlbum = albumId => {
       // console.log(this.props.album)
        return this.props.getAlbum(albumId)
    }

    render(){
        let half = Math.ceil(this.props.album.album.length / 2)
        let part = this.props.album.album.length.splice(0, half)
       // console.log(this.props.album.album.map(al=>console.log(al)))
        return(
            <div className="col-sm-10 col-md-6 col-lg-12">
                <div className="row">

            {this.props.album.album.map(al=><AlbumDetails key={al.id} album={al}/>)}
            </div>
            </div>
        )
    }



}


const mapStateToProps = state => {
    console.log(state)
    return {
        album: state.album
    }

}

const mapDispatchToProps = {
    getOneAlbum
}

export default connect(
    mapStateToProps,
    mapDispatchToProps
)(AlbumContainer)
