import React from 'react';
import {Link} from "react-router-dom"


import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import InputBase from '@material-ui/core/InputBase';
import { fade } from '@material-ui/core/styles/colorManipulator';
import { makeStyles } from '@material-ui/core/styles';
import MenuIcon from '@material-ui/icons/Menu';
import SearchIcon from '@material-ui/icons/Search';
import HomeIcon from "./HomeIcon"
import Avatar from '@material-ui/core/Avatar';
import Grid from '@material-ui/core/Grid';

const useStyles = makeStyles(theme => ({
  root: {
    flexGrow: 1,
  },
  menuButton: {
    marginRight: theme.spacing(2),
  },
  title: {
    flexGrow: 1,
    [theme.breakpoints.up('sm')]: {
      display: 'block',
    },
  },
  search: {
    position: 'relative',
    borderRadius: theme.shape.borderRadius,
    backgroundColor: fade(theme.palette.common.white, 0.15),
    '&:hover': {
      backgroundColor: fade(theme.palette.common.white, 0.25),
    },
    marginLeft: 0,
    width: '100%',
    [theme.breakpoints.up('sm')]: {
      marginLeft: theme.spacing(1),
      width: 'auto',
    },
  },
  avatar: {
    margin: 10,
  },
  bigAvatar: {
    margin: 10,
    width: 20,
    height: 20,
  },
  searchIcon: {
    width: theme.spacing(7),
    height: '100%',
    position: 'absolute',
    pointerEvents: 'none',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
  },
  inputRoot: {
    color: 'inherit',
  },
  inputInput: {
    padding: theme.spacing(1, 1, 1, 7),
    transition: theme.transitions.create('width'),
    width: '100%',
    [theme.breakpoints.up('sm')]: {
      width: 120,
      '&:focus': {
        width: 500,
      },
    },
  },
}));

function SearchAppBar() {
  const classes = useStyles();
  return (
    <div id="nav" className={classes.root}>
      <AppBar position="fixed">
        <Toolbar>
          <IconButton
            edge="start"
            className={classes.menuButton}
            color="inherit"
            aria-label="Open drawer"
          >
            <HomeIcon />
          </IconButton>

          <Typography className={classes.title} variant="h6">
          <a href="/albums" className="album-link">Albums</a> 
          </Typography>

          <div className={classes.search}>
            <div className={classes.searchIcon}>

            </div>
            <Grid container justify="center" alignItems="center">
              {/* <SearchIcon /> */}
              <Avatar alt="Remy Sharp" src="https://www.google.es/url?sa=i&source=images&cd=&ved=2ahUKEwir8Piz-sLiAhUJWxoKHe2jCzYQjRx6BAgBEAU&url=https%3A%2F%2Fpngtree.com%2Ffree-icon%2Favatar_107646&psig=AOvVaw1S9P7fuM3uRUDY49WEtzet&ust=1559295455656829" className={classes.bigAvatar} />
              </Grid>
            {/* <InputBase
              placeholder="Search…"
              classes={{
                root: classes.inputRoot,
                input: classes.inputInput,
              }}
            /> */}
          </div>
        </Toolbar>
      </AppBar>
    </div>
  );
}

export default SearchAppBar;
